comic_2.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-292620/", "cover_date" => "2011-11-01", "date_last_updated" => "2022-06-09 02:35:15 UTC", "deck" => nil, "description" => "<p><i>Meet Miles Morales, the all-new Ultimate Spider-Man!</i></p><table data-max-width=\"true\"><thead><tr><th scope=\"col\">Cover</th><th scope=\"col\">Name</th><th scope=\"col\">Creator(s)</th><th scope=\"col\">Sidebar Location</th></tr></thead><tbody><tr><td>Reg</td><td>Regular Cover</td><td>Kaare Andrews</td><td>1</td></tr><tr><td>Var</td><td>Variant Sketch Cover</td><td>Sara Pichellli</td><td>2</td></tr><tr><td>Var</td><td>Variant Cover</td><td>Sara Pichellli</td><td>4</td></tr><tr><td>Var</td><td>Variant Cover</td><td>Sara Pichelli</td><td>5 &amp; 6</td></tr><tr><td> </td><td> </td><td> </td><td> </td></tr></tbody></table>", "cv_id" => 292620, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/10/105566/2006928-ultimate_spider_man_v2011__1__2011_11____page_1.jpg", "issue_number" => 1.0, "name" => "All-New Spider-Man", "site_detail_url" => "https://comicvine.gamespot.com/ultimate-comics-spider-man-1-all-new-spider-man/4000-292620/", "store_date" => "2011-09-14"})
