comic_9.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-280828/", "cover_date" => "2011-09-30", "date_last_updated" => "2018-04-30 21:28:36 UTC", "deck" => nil, "description" => "<p>The first story - <b>\"Captain America\"</b> - about talk aunt May with Steven Rogers.</p><p>The second - <b>\"Thor\"</b> - about life after death.</p><p>The third - <b>\"Rogue\"</b> - about this ex X-man and her feeling about the death of friends.</p>", "cv_id" => 280828, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/2/27994/1922917-ed30a9586fc8e039831588cd4212ae2769399090_cover_large.jpg", "issue_number" => 2.0, "name" => "Chapter Two of Six", "site_detail_url" => "https://comicvine.gamespot.com/ultimate-fallout-2-chapter-two-of-six/4000-280828/", "store_date" => "2011-07-20"})
