comic_14.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-503588/", "cover_date" => "2015-10-31", "date_last_updated" => "2017-06-17 09:04:21 UTC", "deck" => nil, "description" => "<p><em>“REBOOT?” Part One! This one is big, folks. We're going back to square one. Mark suddenly finds himself without powers. BACK HOME...but aware of everything he's lived through. What does he change, who can he save...and how will he deal with his father now that he's knows what's coming?</em></p>", "cv_id" => 503588, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/6/67663/4872393-124.jpg", "issue_number" => 124.0, "name" => "Reboot? Part One of Three", "site_detail_url" => "https://comicvine.gamespot.com/invincible-124-reboot-part-one-of-three/4000-503588/", "store_date" => "2015-10-21"})
comic_14.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-506222/", "cover_date" => "2015-11-30", "date_last_updated" => "2017-06-17 10:46:10 UTC", "deck" => nil, "description" => "<p><em>“REBOOT?” Part Two.</em></p><p><em>Invincible’s father, Omni-Man, murdered The Guardians of the Globe to facilitate his attempt to take over Earth... now that he has the opportunity can Invincible stop his father and change the course of history... for the better?</em></p>", "cv_id" => 506222, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/6/67663/4913113-125.jpg", "issue_number" => 125.0, "name" => "Reboot? Part Two of Three", "site_detail_url" => "https://comicvine.gamespot.com/invincible-125-reboot-part-two-of-three/4000-506222/", "store_date" => "2015-11-18"})
comic_14.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-508474/", "cover_date" => "2015-12-31", "date_last_updated" => "2017-06-17 10:56:03 UTC", "deck" => nil, "description" => "<p><em>“REBOOT?” Part Three! CONCLUSION! Invincible’s world comes crashing down around him leaving him with the same question we all have... where do we go from here?</em></p>", "cv_id" => 508474, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/6/67663/4956185-126.jpg", "issue_number" => 126.0, "name" => "Reboot? Part Three of Three", "site_detail_url" => "https://comicvine.gamespot.com/invincible-126-reboot-part-three-of-three/4000-508474/", "store_date" => "2015-12-16"})
comic_14.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-526091/", "cover_date" => "2016-04-30", "date_last_updated" => "2017-06-17 11:27:17 UTC", "deck" => nil, "description" => "<p><em>NEW STORY ARC</em></p><p><em>Mark has returned from the past... to a very different future. Now he has to pick up the pieces of his life and try to put them back together. INVINCIBLE co-creator CORY WALKER returns to the book to set the stage for the next chapter of Invincible’s life!</em></p>", "cv_id" => 526091, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/6/67663/5160398-127.jpg", "issue_number" => 127.0, "name" => "Issue #127", "site_detail_url" => "https://comicvine.gamespot.com/invincible-127/4000-526091/", "store_date" => "2016-04-20"})
comic_14.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-530757/", "cover_date" => "2016-05-31", "date_last_updated" => "2017-06-17 11:27:38 UTC", "deck" => nil, "description" => "<p><em>Allen the Alien is under attack. Has Mark’s absence put all his friends and family in danger? Will he be able to put things right before it’s too late?</em></p>", "cv_id" => 530757, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/6/67663/5212686-128.jpg", "issue_number" => 128.0, "name" => "Issue #128", "site_detail_url" => "https://comicvine.gamespot.com/invincible-128/4000-530757/", "store_date" => "2016-05-18"})
comic_14.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-535385/", "cover_date" => "2016-06-30", "date_last_updated" => "2017-06-17 11:23:48 UTC", "deck" => nil, "description" => "<p><em>CORY WALKER’s epic stint as artist continues! Thragg is making moves, conquering more worlds as he rebuilds the Viltrum Empire. Meanwhile, Mark, Eve, and Terra take a trip to Earth to visit Grandma and Grandpa.</em></p>", "cv_id" => 535385, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/6/67663/5264931-129.jpg", "issue_number" => 129.0, "name" => "Issue #129", "site_detail_url" => "https://comicvine.gamespot.com/invincible-129/4000-535385/", "store_date" => "2016-06-15"})
comic_14.issues.create!({"currency" => usd, "aliases" => nil, "api_detail_url" => "https://comicvine.gamespot.com/api/issue/4000-546076/", "cover_date" => "2016-08-31", "date_last_updated" => "2018-03-31 13:45:54 UTC", "deck" => nil, "description" => "<p><em>Mark never wanted to return to Earth… but now he finds himself face-to-face with Robot, and no matter how hard he tries, he just can’t forget all the evil he has done. This could get ugly.</em></p>", "cv_id" => 546076, "image" => "https://comicvine.gamespot.com/a/uploads/scale_medium/6/67663/5396637-130.jpg", "issue_number" => 130.0, "name" => "Issue #130", "site_detail_url" => "https://comicvine.gamespot.com/invincible-130/4000-546076/", "store_date" => "2016-08-24"})
